try:
	sheets = value.get("sheets", [])

	options = []
	for sheet in sheets:
		props = sheet.get("properties", {})
		name = props.get("title")
		if name is not None:
			options.append({"value": name, "label": name})

	return options
except Exception as e:
	return []

